import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Companies = () => {
  const [companies, setCompanies] = useState([]);
  const [companyName, setCompanyName] = useState('');
  const [companyEmail, setCompanyEmail] = useState('');
  const [companyReps, setCompanyReps] = useState('');
  const [representatives, setRepresentatives] = useState([]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('/api/companies', { 
        name: companyName, 
        email: companyEmail, 
        reps: companyReps,
        representatives: representatives
      });
      setCompanies([...companies, response.data]);
      setCompanyName('');
      setCompanyEmail('');
      setCompanyReps('');
      setRepresentatives([]);
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddRepresentative = (event) => {
    event.preventDefault();
    setRepresentatives([...representatives, '']);
  };

  const handleRepresentativeChange = (event, index) => {
    const updatedRepresentatives = [...representatives];
    updatedRepresentatives[index] = event.target.value;
    setRepresentatives(updatedRepresentatives);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/companies');
        setCompanies(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>List of Companies Coming</h1>
      <ul>
        {companies.map((company) => (
          <li key={company.id}>
            {company.name} ({company.email}) - {company.reps} reps:
            <ul>
              {company.representatives.map((representative) => (
                <li key={representative}>{representative}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={companyName} onChange={(event) => setCompanyName(event.target.value)} />
        </label>
        <label>
          Email:
          <input type="email" value={companyEmail} onChange={(event) => setCompanyEmail(event.target.value)} />
        </label>
        <label>
          Number of representatives:
          <input type="number" value={companyReps} onChange={(event) => setCompanyReps(event.target.value)} />
        </label>
        {representatives.map((representative, index) => (
          <label key={index}>
            Representative #{index + 1} name:
            <input type="text" value={representative} onChange={(event) => handleRepresentativeChange(event, index)} />
          </label>
        ))}
        <button type="button" onClick={handleAddRepresentative}>
          Add representative
        </button>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Companies;
